body.script:
// === Matrix Rain Effect ===
const canvas = document.getElementById('matrix');
const ctx = canvas.getContext('2d');

// full-screen canvas
canvas.width  = window.innerWidth;
canvas.height = window.innerHeight;

// characters used (binary)
const characters = '01';

// convert string to array
const chars = characters.split('');

// font size and number of columns
const fontSize = 16;
const columns = canvas.width / fontSize;

// y-position for each column
const drops = Array(Math.floor(columns)).fill(1);

// draw loop
function draw() {
  // translucent background for trail effect
  ctx.fillStyle = 'rgba(0, 0, 0, 0.05)';
  ctx.fillRect(0, 0, canvas.width, canvas.height);

  // text style
  ctx.fillStyle = '#00ff66'; // neon green
  ctx.font = fontSize + 'px monospace';

  // draw characters
  for (let i = 0; i < drops.length; i++) {
    const text = chars[Math.floor(Math.random() * chars.length)];
    ctx.fillText(text, i * fontSize, drops[i] * fontSize);

    // send drop back to top randomly after it crosses screen
    if (drops[i] * fontSize > canvas.height && Math.random() > 0.975) {
      drops[i] = 0;
    }
    drops[i]++;
  }
}

// animate at ~30fps
setInterval(draw, 35);

// handle window resize
window.addEventListener('resize', () => {
  canvas.width  = window.innerWidth;
  canvas.height = window.innerHeight;
});
